export default {
    namespaced: true,
    state: {
        initiatives: []
    },
    getters: {
        // itemCount: state => state.lines.reduce((total, line) =>
        //     total + line.quantity, 0),
        // totalPrice: state => state.lines.reduce((total, line) =>
        // total + (line.quantity * line.product.price), 0)
    },
    mutations: {
        addInitiative(state, initiative) {
            let line = state.initiatives.find(line => line.initiative.id == initiative.id);
            if (line != null) {
                // line.quantity++;
            } else {
                state.initiatives.push({
                    initiative
                });
            }
        },
        // changeQuantity(state, update) {
        //     update.line.quantity = update.quantity;
        // },
        removeInitiative(state, lineToRemove) {
            let index = state.initiatives.findIndex(line => line == lineToRemove);
            if (index > -1) {
                state.initiatives.splice(index, 1);
            }
        },
        setInitiativeData(state, data) {
            state.initiatives = data;
        }
    },
    actions: {
        loadInitiativeData(context) {
            let data = localStorage.getItem("initiative");

