import { IconCheckbox, Modal } from "@/components";
import { Pagination } from "@/components";
import targets from "./targets";
import Fuse from "fuse.js";
import Swal from "sweetalert2";
import moment from "moment";

export default {
  components: {
    IconCheckbox,
    Modal
  },
  computed: {
    /***
     * Returns a page from the searched data or the whole data. Search is performed in the watch section below
     */
    queriedData() {
      let result = this.tableData;
      if (this.searchedData.length > 0) {
        result = this.searchedData;
      }
      return result.slice(this.from, this.to);
    },
    to() {
      let highBound = this.from + this.pagination.perPage;
      if (this.total < highBound) {
        highBound = this.total;
      }
      return highBound;
    },
    from() {
      return this.pagination.perPage * (this.pagination.currentPage - 1);
    },
    total() {
      return this.searchedData.length > 0
        ? this.searchedData.length
        : this.tableData.length;
    }
  },
  data() {
    return {
      currentSort: "name",
      currentSortOrder: "asc",
      pagination: {
        perPage: 5,
        currentPage: 1,
        perPageOptions: [5, 10, 25, 50],
        total: 0
      },
      footerTable: ["Quarter", "From", "To", "Target", "Actions"],
      searchQuery: "",
      propsToSearch: ["quarterYear", "from", "to", "target"],
      tableData: targets,
      searchedData: [],
      fuseSearch: null,
      showDialog: false,
      fromModal: null,
      toModal: null,
      year: null,
      target: "",
      quarter: null,
      itemId: null
    };
  },
  methods: {
    displayAddNew() {
      this.fromModal = moment().format("YYYY/MM/DD");
      this.toModal = moment().format("YYYY/MM/DD");
      this.year = null;
      this.target = null;
      this.quarter = null;
      this.itemId = null;
      this.showDialog = true;
    },
    modalClick() {
      this.showDialog = false;

      if (this.itemId == null) {
        this.tableData.push({
          id: targets.length + 1,
          quarterYear: `${this.quarter} ${this.year}`,
          quarter: this.quarter,
          year: this.year,
          from: moment(this.fromModal).format("D MMM YYYY"),
          to: moment(this.toModal).format("D MMM YYYY"),
          target: this.target
        });
      } else {
        let indexToUpdate = this.tableData.findIndex(
          tableRow => tableRow.id === this.itemId
        );

        if (indexToUpdate >= 0) {
          this.tableData.splice(indexToUpdate, 1);
        }

        this.tableData.push({
          id: this.itemId,
          quarterYear: `${this.quarter} ${this.year}`,
          quarter: this.quarter,
          year: this.year,
          from: moment(this.fromModal).format("D MMM YYYY"),
          to: moment(this.toModal).format("D MMM YYYY"),
          target: this.target
        });
      }
    },
    validate() {
      this.$emit("on-validated", true, this.model);
      return Promise.resolve(true);
    },
    customSort(value) {
      return value.sort((a, b) => {
        const sortBy = this.currentSort;
        if (this.currentSortOrder === "desc") {
          return a[sortBy].localeCompare(b[sortBy]);
        }
        return b[sortBy].localeCompare(a[sortBy]);
      });
    },
    handleLike(item) {
      Swal.fire({
        title: `You liked ${item.name}`,
        buttonsStyling: false,
        type: "success",
        confirmButtonClass: "md-button md-success"
      });
    },
    handleEdit(item) {
      this.quarter = item.quarter;
      this.year = item.year;
      this.fromModal = moment(item.from, "D MMM YYYY").format("YYYY/MM/DD");
      this.toModal = moment(item.to, "D MMM YYYY").format("YYYY/MM/DD");
      this.target = item.target;
      this.itemId = item.id;
      this.showDialog = true;
    },
    handleDelete(item) {
      Swal.fire({
        title: "Are you sure?",
        text: `You won't be able to revert this!`,
        type: "warning",
        showCancelButton: true,
        confirmButtonClass: "md-button md-success btn-fill",
        cancelButtonClass: "md-button md-danger btn-fill",
        confirmButtonText: "Yes, delete it!",
        buttonsStyling: false
      }).then(result => {
        if (result.value) {
          this.deleteRow(item);
          Swal.fire({
            title: "Deleted!",
            text: `You deleted ${item.quarter}`,
            type: "success",
            confirmButtonClass: "md-button md-success btn-fill",
            buttonsStyling: false
          });
        }
      });
    },
    deleteRow(item) {
      let indexToDelete = this.tableData.findIndex(
        tableRow => tableRow.id === item.id
      );
      if (indexToDelete >= 0) {
        this.tableData.splice(indexToDelete, 1);
      }
    }

  },
  mounted() {
    // Fuse search initialization.
    this.fuseSearch = new Fuse(this.tableData, {
      keys: ["quarterYear", "target"],
      threshold: 0.3
    });
  },
  watch: {
    /**
     * Searches through the table data by a given query.
     * NOTE: If you have a lot of data, it's recommended to do the search on the Server Side and only display the results here.
     * @param value of the query
     */
    searchQuery(value) {
      let result = this.tableData;
      if (value !== "") {
        result = this.fuseSearch.search(this.searchQuery);
      }
      this.searchedData = result;
    }
  }
};
